type HeroIconName = Extract<
  Exclude<
    NgIconName,
    `hero${string}Solid` | `hero${string}Mini` | `hero${string}Micro`
  >,
  `hero${string}`
>;

type LucideIconName = Extract<NgIconName, `lucide${string}`>;

